//// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract ThrustpadInstantAirdrop is Ownable, ReentrancyGuard {
    IERC20 public token;
    bytes32 public merkleRoot;
    mapping(address => bool) public claimed;

    event Claimed(address indexed account, uint256 amount);

    constructor(address _token, bytes32 _merkleRoot) Ownable(tx.origin) {
        token = IERC20(_token);
        merkleRoot = _merkleRoot;
    }

    function claim(
        uint256 amount,
        bytes32[] calldata merkleProof
    ) external nonReentrant {
        require(
            !claimed[msg.sender],
            "ThrustpadInstantAirdrop: Account already claimed"
        );
        require(
            MerkleProof.verify(
                merkleProof,
                merkleRoot,
                keccak256(abi.encodePacked(msg.sender, amount))
            ),
            "ThrustpadInstantAirdrop: Invalid proof"
        );

        claimed[msg.sender] = true;
        token.transfer(msg.sender, amount);

        emit Claimed(msg.sender, amount);
    }

    function setMerkleRoot(bytes32 _merkleRoot) external onlyOwner {
        merkleRoot = _merkleRoot;
    }
}

//// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "./ThrustpadInstantAirdrop.sol";

contract ThrustpadInstantAirdropFactory {
    mapping(address => address[]) public deployedAirdrops;

    event AirdropCreated(address indexed airdrop, address indexed token);

    function createAirdrop(
        address _token,
        bytes32 _merkleRoot,
        uint256 totalAmount
    ) external returns (address) {
        require(
            totalAmount > 0,
            "ThrustpadInstantAirdropFactory: Invalid amount"
        );
        address newAirdrop = address(
            new ThrustpadInstantAirdrop(_token, _merkleRoot)
        );

        IERC20(_token).transferFrom(
            msg.sender,
            address(newAirdrop),
            totalAmount
        );

        emit AirdropCreated(newAirdrop, _token);

        deployedAirdrops[msg.sender].push(newAirdrop);

        return newAirdrop;
    }

    function getAddressCreate2(
        bytes memory bytecode,
        uint256 salt
    ) public view returns (address) {
        bytes32 hash = keccak256(
            abi.encodePacked(
                bytes1(0xff),
                address(this),
                salt,
                keccak256(bytecode)
            )
        );

        return address(uint160(uint(hash)));
    }

    function getBytecode(
        address _token,
        bytes32 _merkleRoot,
        uint256 totalAmount
    ) public pure returns (bytes memory) {
        bytes memory bytecode = type(ThrustpadInstantAirdrop).creationCode;

        return
            abi.encodePacked(
                bytecode,
                abi.encode(_token, _merkleRoot, totalAmount)
            );
    }

    function getdeployedAirdropsLen(
        address creator
    ) public view returns (uint256) {
        return deployedAirdrops[creator].length;
    }

    function getdeployedAirdrops(
        address creator
    ) public view returns (address[] memory) {
        return deployedAirdrops[creator];
    }
}

